/*=========================================================AJUSTE DE DATAS=====================================================================================================================================*/

SELECT *  FROM CT2010 WHERE CT2_CREDIT = '25101010100002'  AND D_E_L_E_T_=' '  AND CT2_DATA BETWEEN '20150101'AND'20150331' AND CT2_FILIAL = '11056' ORDER BY CT2_DATA;
UPDATE CT2010 SET CT2_DATA = '201411'||SUBSTR(CT2_DATA,7,2) WHERE CT2_CREDIT = '25101010100002' AND D_E_L_E_T_=' ' AND CT2_DATA BETWEEN '20150201'AND'20150231' AND CT2_FILIAL = '11056';



