SELECT SUM(CT2_VALOR) CREDITO FROM CT2010 WHERE CT2_CREDIT = '11201010100001  ' AND D_E_L_E_T_=' ' AND CT2_FILIAL = '11049' AND CT2_ITEMC = 'C00810301' AND CT2_DATA BETWEEN'20150101'AND'20151231';
UPDATE CT2010 SET CT2_CREDIT = '11202010100001' WHERE CT2_CREDIT = '11201010100001  ' AND D_E_L_E_T_=' ' AND CT2_FILIAL = '11049' AND CT2_ITEMC = 'C00810301' AND CT2_DATA BETWEEN'20150101'AND'20151231'; 

SELECT SUM(CT2_VALOR) DEBITO FROM CT2010 WHERE CT2_DEBITO = '11201010100001  ' AND D_E_L_E_T_=' ' AND CT2_FILIAL = '11049' AND CT2_ITEMD = 'C00810301' AND CT2_DATA BETWEEN'20150101'AND'20151231';
