SELECT B.A2_COD,B.A2_LOJA,A.A2_COD,B.A2_NOME,B.A2_CGC FROM SA2010 A LEFT OUTER JOIN SA2010 B ON SUBSTR(A.A2_CGC,1,8) = SUBSTR(B.A2_CGC,1,8) AND A.A2_COD != B.A2_COD AND B.D_E_L_E_T_=' '
WHERE A.D_E_L_E_T_=' ' AND A.A2_CGC != ' ' AND A.A2_CGC NOT IN ('00000000000000','00000000000191') AND A.A2_TIPO = 'J' AND B.A2_COD IS NOT NULL GROUP BY B.A2_COD,B.A2_LOJA,A.A2_COD,B.A2_NOME,B.A2_CGC  ;