SELECT A1_FILIAL,MAX(A1_COD), 'SA1' TABELA FROM SA1010 WHERE LENGTH(TRIM(A1_COD)) = 6 AND A1_COD < '999999' AND SUBSTR(A1_COD,6,1) BETWEEN '0' AND '9' AND D_E_L_E_T_=' ' GROUP BY A1_FILIAL ORDER BY A1_FILIAL;

SELECT A2_FILIAL,MAX(A2_COD), 'SA2' TABELA FROM SA2010 WHERE LENGTH(TRIM(A2_COD)) = 6 AND A2_COD < '999999' AND SUBSTR(A2_COD,6,1) BETWEEN '0' AND '9' AND D_E_L_E_T_=' ' GROUP BY A2_FILIAL ORDER BY A2_FILIAL;

SELECT B1_FILIAL,MAX(B1_COD), 'SB1' TABELA FROM SB1010 WHERE LENGTH(TRIM(B1_COD)) = 15 AND B1_COD < '999999999999999' AND SUBSTR(B1_COD,15,1) BETWEEN '0' AND '9' AND D_E_L_E_T_=' ' GROUP BY B1_FILIAL ORDER BY B1_FILIAL;

SELECT CN9_FILIAL,MAX(CN9_NUMERO), 'CN9' TABELA FROM CN9010 WHERE LENGTH(TRIM(CN9_NUMERO)) = 15 AND SUBSTR(CN9_NUMERO,15,1) BETWEEN '0' AND '9' AND CN9_NUMERO < '999999999999999' AND CN9_FILIAL IN ('11032','11033','11040','11041') AND D_E_L_E_T_=' ' GROUP BY CN9_FILIAL ORDER BY CN9_FILIAL;

SELECT C5_FILIAL,MAX(C5_NUM), 'SC5' TABELA FROM SC5010 WHERE LENGTH(TRIM(C5_NUM)) = 6 AND SUBSTR(C5_NUM,6,1) BETWEEN '0' AND '9' AND C5_NUM < '999999' AND C5_FILIAL IN ('11034','11049') AND D_E_L_E_T_=' ' GROUP BY C5_FILIAL ORDER BY C5_FILIAL;